import com.eviware.soapui.impl.wsdl.teststeps.*
import com.eviware.soapui.impl.wsdl.teststeps.WsdlTestRequestStep
import com.eviware.soapui.support.types.StringToStringMap
def envSwitch(env)
{
 switch (env)
{
case 'DEV':
 project.setPropertyValue("EndpointUrl", "http://www.webservicex.com/globalweather.asmx")
 break
 case 'SIT':
 project.setPropertyValue("EndpointUrl", "http://www.webservicex.com/globalweather.asmx")
 break
 default:
 project.setPropertyValue("EndpointUrl", "http://www.webservicex.com/globalweather.asmx") 
 break
}
}

def envVar = project.getPropertyValue("Environment")
log.info envVar
def endpoint = project.getPropertyValue("EndpointUrl")
envSwitch (envVar)
project.testSuiteList.each { suite ->
        suite.testCaseList.each { testcase ->
        testcase.testStepList.each { teststep ->
        if (teststep instanceof WsdlTestRequestStep )
        teststep.setPropertyValue("Endpoint", endpoint)

        }
        }
}
